import React, { useState } from 'react';
import PropTypes from "prop-types";
import { StyleSheet, TouchableOpacity } from 'react-native';
import { Container, Content, View } from "native-base";
import { Col, Row, Grid } from "react-native-easy-grid";

import Error from "../../components/Error";
import Header from "../../components/Header";
import Footer from "../../components/Footer";
import Button from "../../components/Button";
import TextField from '../../components/TextField';
import Typography from '../../components/Typography';
import global from "../../utils/GlobalStyles";

const styles = StyleSheet.create({
  textContainer: {
    marginTop: 89,
    marginLeft: 24,
    marginRight: 30,
  },
  headerText: {
    lineHeight: 30,
    marginTop: 4,
    fontSize: 24,
    fontFamily: 'SharpSansSemibold',
  },
  lineText: {
    lineHeight: 30,
    marginTop: 1.5,
    fontSize: 24,
    fontFamily: 'SharpSansSemibold',
  },
  fieldContainer: {
    marginLeft: 5,
    flexDirection: 'row',
    marginTop: 50,
  },
  hr: {
    backgroundColor: 'rgba(197,0,6,1)',
    height: 1.3,
    width: '100%',
  },
  errorIcon: {
    height: 20,
    width: 20,
  },
  textError: {
    marginLeft: 5,
    color: 'rgba(197,0,6,1)',
  },
});

const Email = ({ navigation }) => {
  const [focus, setFocus] = useState({ email: false });
  const [errorText, setErrorText] = useState("");
  const [isDisabled, setDisabled] = useState(true);
  const [emailValue, setEmailValue] = useState("");

  const onFocus = (_fieldName) => {
    setFocus({ ...focus, [_fieldName]: true });
  };

  const onBlur = (_fieldName) => {
    setFocus({ ...focus, [_fieldName]: false });
  };

  const verifyEmail = (email) => {
    setEmailValue(email);

    const emailRegex = /^(([^<>()\\[\]\\.,;:\s@"]+(\.[^<>()\\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
    if (email === "") {
      setErrorText("");
    } else if (emailRegex.test(email)) {
      if (email === "hey@singlife.com") {
        setErrorText("");
        setDisabled(false);
      } else {
        setErrorText("This email is already taken.");
        setDisabled(true);
      }
    } else {
      setErrorText("Please enter a valid email address.");
      setDisabled(false);
    }
  };

  return (
    <Container>
      <Header progress progressVal={75} back backAction={() => navigation.goBack()} transparent />

      <Content style={global.wrapper}>
        <View style={global.content}>
          <Typography type="title2" text="What's your email?" />

          <View style={global.body}>
            <Typography type="body" text="Your email address will serve as your username. Assign an email address that you will keep for a long time." />
          </View>

          <View style={global.body}>
            <TextField
              label="Email"
              focus={focus.email}
              error={errorText !== ""}
              value={emailValue}
              onChangeText={(text) => verifyEmail(text)}
              onFocus={() => onFocus("email")}
              onBlur={() => {
                onBlur("email");
                setFocus({ ...focus, email: false });
              }}
            />

            {errorText !== ""
            && (
            <View>
              <Error text={errorText} />

              {errorText === "This email is already taken."
            && (
              <Grid style={[global.body, { marginTop: 24 }]}>
                <Row size={4} style={{ paddingLeft: 50 }}>
                  <Col style={global.row}>
                    <Typography text="Please enter another mobile number or " type="body" />
                    <TouchableOpacity style={global.link}><Typography text="log in" type="body-link" /></TouchableOpacity>
                    <Typography text="." type="body" />
                  </Col>
                </Row>
              </Grid>
            )}
            </View>
            )}
          </View>
        </View>
      </Content>

      <Footer>
        <Button disabled={isDisabled} onPress={() => navigation.navigate("Password")} full variant="primary-contained" text="Next" />
      </Footer>
    </Container>
  );
};

Email.propTypes = {
  navigation: PropTypes.objectOf(PropTypes.any).isRequired,
};

export default Email;
