import React, { useState } from 'react';
import { StyleSheet } from 'react-native';
import { Container, Content, View } from "native-base";
import Header from "../../components/Header";
import Footer from "../../components/Footer";
import Button from "../../components/Button";
import MobileField from '../../components/MobileField';
import Typography from '../../components/Typography';
import global from "../../utils/GlobalStyles";

const styles = StyleSheet.create({
  textContainer: {
    marginTop: 89,
    marginLeft: 24,
    marginRight: 30,
  },
  headerText: {
    lineHeight: 30,
    marginTop: 4,
    fontSize: 24,
    fontFamily: 'SharpSansSemibold',
  },
  lineText: {
    lineHeight: 30,
    marginTop: 1.5,
    fontSize: 24,
    fontFamily: 'SharpSansSemibold',
  },
  fieldContainer: {
    marginLeft: 5,
    flexDirection: 'row',
    marginTop: 0,
  },
  infoContainer: {
    marginTop: 33,
  },
});

const mobileNumber = ({ navigation }) => {
  const [mobileNum, setMobileNum] = useState("");
  const [isValid, setIsValid] = useState(false);

  return (
    <Container>
      <Header back transparent />

      <Content style={global.wrapper}>
        <View style={global.content}>
          <View style={styles.fieldContainer}>
            <MobileField enableNext={setIsValid} onChangeMobileNum={setMobileNum} mobileValue={mobileNum} />
          </View>
          <View style={styles.infoContainer}>
            <Typography style={{ color: '#000 '}} type="body" text="We'll need to verify your new mobile number when you update it." />
            <Typography style={{ color: '#000 ', marginTop: 20 }} type="body" text="Make sure to provide your latest contact details. This will be used for all future transactions and communications. " />
          </View>
        </View>
      </Content>

      <Footer>
        <Button onPress={() => navigation.navigate("Otp")} disabled={!isValid} full variant="default-outlined" text="Next" />
      </Footer>
    </Container>
  );
};

export default mobileNumber;
