import React, { useState } from "react";
import PropTypes from "prop-types";
import { Container, Content, View } from "native-base";
import { Col, Row, Grid } from "react-native-easy-grid";

import Header from "../../components/Header";
import Footer from "../../components/Footer";
import Typography from "../../components/Typography";
import Button from "../../components/Button";
import TextField from "../../components/TextField";
import Error from "../../components/Error";

import global from "../../utils/GlobalStyles";

export default function ResetEmail({ navigation }) {
  const emailRegex = /^(([^<>()\\[\]\\.,;:\s@"]+(\.[^<>()\\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
  const [focus, setFocus] = useState({ email: false });
  const [value, setValue] = useState("");
  const [error, setError] = useState(null);
  const [errorText, setErrorText] = useState("");

  const onFocus = (_fieldName) => {
    setFocus({ ...focus, [_fieldName]: true });
  };

  const onBlur = (_fieldName) => {
    setFocus({ ...focus, [_fieldName]: false });
  };

  const onChange = (_value) => {
    setValue(_value);

    if (_value !== "") {
      if (emailRegex.test(_value)) {
        if (_value !== "neri@singlife.com") {
          setError(true);
          setErrorText("Please enter a registered email address.");
        } else {
          setError(false);
        }
      } else {
        setError(true);
        setErrorText("Please enter a valid email address.");
      }
    } else {
      setError(null);
      setErrorText("");
    }
  };

  return (
    <Container>
      <Header logo menu close closeAction={() => navigation.navigate("Login")} transparent />

      <Content style={global.wrapper}>
        <View style={global.content}>
          <Typography text="Reset Password" type="title2" />

          <Grid style={global.body}>
            <Row size={4}>
              <Col>
                <Typography text="Enter your email address below so we can verify it's you." type="body" />
              </Col>
            </Row>
          </Grid>

          <Grid style={global.body}>
            <Row size={4}>
              <Col>
                <TextField
                  label="Emails"
                  error={error}
                  focus={focus.email}
                  onFocus={() => onFocus("email")}
                  onBlur={() => {
                    onBlur("email");
                  }}
                  value={value}
                  onChangeText={(_input) => onChange(_input)}
                />

                {error && (
                  <Error text={errorText} />
                )}
              </Col>
            </Row>
          </Grid>
        </View>
      </Content>

      <Footer>
        <Button full disabled={error === null || error} onPress={() => navigation.navigate("EmailVerification")} variant="primary-contained" text="Send" />
      </Footer>
    </Container>
  );
}

ResetEmail.propTypes = {
  navigation: PropTypes.objectOf(PropTypes.any).isRequired,
};
