import React from "react";
import PropTypes from "prop-types";
import { StyleSheet, Text } from "react-native";
import { Button } from "native-base";
import color from "../utils/Color";

const styles = StyleSheet.create({
  root: {
    borderRadius: 4,
    elevation: 0,
    height: 50,
  },
  text: {
    fontFamily: "SharpSansSemibold",
    fontSize: 16,
    letterSpacing: 1,
    textTransform: "uppercase",
  },
  containedPrimary: {
    backgroundColor: color.orangeRed,
  },
  containedText: {
    color: color.white,
  },
  containedPrimaryDisabled: {
    backgroundColor: color.orangeRed,
    opacity: 0.5,
  },
  outlinedDefault: {
    backgroundColor: color.white,
    borderWidth: 1,
    borderColor: color.black,
  },
  outlinedText: {
    color: color.black,
  },
  outlinedDefaultDisabled: {
    backgroundColor: color.white,
    opacity: 0.5,
  },
});

function CustomButton({ text, variant, ...props }) {
  const properties = { ...props };

  const setButtonStyling = () => {
    switch (variant) {
      case "default-outlined":
        return styles.outlinedDefault;
      default:
        return styles.containedPrimary;
    }
  };

  const setTextStyling = () => {
    switch (variant) {
      case "default-outlined":
        return styles.outlinedText;
      default:
        return styles.containedText;
    }
  };

  const setDisabledStyling = () => {
    switch (variant) {
      case "default-outlined":
        if (properties.disabled) return styles.outlinedDefaultDisabled;
        return "";
      default:
        if (properties.disabled) return styles.containedPrimaryDisabled;
        return "";
    }
  };

  return (
    <Button style={[styles.root, setButtonStyling(), setDisabledStyling()]} {...props}>
      <Text style={[styles.text, setTextStyling()]}>{text}</Text>
    </Button>
  );
}

CustomButton.propTypes = {
  text: PropTypes.string.isRequired,
  variant: PropTypes.string,
};

CustomButton.defaultProps = {
  variant: "primary-contained",
};

export default CustomButton;
