import React, { useState } from "react";
import PropTypes from "prop-types";
import { Container, Content, View } from "native-base";
import { Col, Row, Grid } from "react-native-easy-grid";

import { Formik } from "formik";
import * as Yup from "yup";

import Header from "../../components/Header";
import Footer from "../../components/Footer";
import Typography from "../../components/Typography";
import Button from "../../components/Button";
import TextField from "../../components/TextField";

import global from "../../utils/GlobalStyles";

export default function NickName({ navigation }) {
  const [focus, setFocus] = useState({ nick_name: false });

  const [initialValues, setInitialValues] = useState({
    nickname: "",
  });

  const onFocus = (_fieldName) => {
    setFocus({ ...focus, [_fieldName]: true });
  };

  const onBlur = (_fieldName) => {
    setFocus({ ...focus, [_fieldName]: false });
  };

  const onSubmit = (_values) => {
    navigation.navigate("OnboardingFatca");
  };

  return (
    <Container>
      <Header progress back backAction={() => navigation.goBack()} transparent />

      <Formik
        enableReinitialize
        initialValues={initialValues}
        onSubmit={(values, { setSubmitting }) => {
          setTimeout(() => {
            onSubmit(values);
            setSubmitting(false);
          }, 400);
        }}
      >
        {({
          values,
          handleBlur,
          setFieldValue,
          handleSubmit,
        }) => (
          <React.Fragment>
            <Content style={global.wrapper}>
              <View style={global.content}>
