                <Grid style={global.body}>
                  <Row size={4}>
                    <Col>
                      <TextField
                        maxLength={20}
                        label="Nickname"
                        focus={focus.nickname}
                        value={values.nickname}
                        onFocus={() => onFocus("nickname")}
                        onBlur={() => {
                          onBlur("nickname");
                          handleBlur("nickname");
                        }}
                        onChangeText={(value) => {
                          const regex = new RegExp(/^[a-zA-Z0-9]*$/);

                          if (regex.test(value)) {
                            setFieldValue("nickname", value);
                          }
                        }}
                      />
                    </Col>
                  </Row>
                </Grid>
              </View>
            </Content>

            <Footer>
              <Button onPress={handleSubmit} disabled={values.nickname === ""} full variant="primary-contained" text="Next" />
            </Footer>
          </React.Fragment>
        )}
      </Formik>
    </Container>
  );
}

NickName.propTypes = {
  navigation: PropTypes.objectOf(PropTypes.any).isRequired,
};
