import React, { useState } from "react";
import PropTypes from "prop-types";
import { Container, Content, View } from "native-base";
import { Col, Row, Grid } from "react-native-easy-grid";

import Header from "../../components/Header";
import Footer from "../../components/Footer";
import Button from "../../components/Button";
import ActiveButton from "../../components/ActiveButton";
import Typography from "../../components/Typography";

import global from "../../utils/GlobalStyles";

export default function Gender({ navigation }) {
  const [gender, setGender] = useState("male");

  const selectGender = (_option) => {
    setGender(_option);
  };

  return (
    <Container>
      <Header progress back backAction={() => navigation.goBack()} transparent />

      <Content style={global.wrapper}>
        <View style={global.content}>
          <Typography type="title2" text="Whatâ€™s your gender?" />

          <Grid>
            <Row size={4} style={global.body}>
              <Col style={{ paddingRight: 10 }}>
                <ActiveButton onPress={() => selectGender("male")} full active={gender === "male"} text="Male" />
              </Col>

              <Col style={{ paddingLeft: 10 }}>
                <ActiveButton onPress={() => selectGender("female")} full active={gender === "female"} variant="default-outlined" text="Female" />
              </Col>
            </Row>
          </Grid>
        </View>
      </Content>

      <Footer>
        <Button onPress={() => navigation.navigate("OnboardingMobile")} full variant="primary-contained" text="Next" />
      </Footer>
    </Container>
  );
}

Gender.propTypes = {
  navigation: PropTypes.objectOf(PropTypes.any).isRequired,
};
