import React from "react";
import PropTypes from "prop-types";
import { StyleSheet, Text } from "react-native";
import { Button } from "native-base";
import color from "../utils/Color";

const styles = StyleSheet.create({
  root: {
    backgroundColor: color.white,
    borderWidth: 1,
    borderRadius: 4,
    elevation: 0,
    height: 50,
  },
  text: {
    fontFamily: "SharpSansSemibold",
    fontSize: 16,
    letterSpacing: 1,
    textTransform: "uppercase",
  },
  outlinedDefault: {
    borderColor: color.black,
  },
  outlinedText: {
    color: color.black,
  },
  active: {
    borderColor: color.orangeRed,
  },
  activeText: {
    color: color.orangeRed,
  },
});

function ActiveButton({ text, active, ...props }) {
  return (
    <Button style={[styles.root, active ? styles.active : styles.outlinedDefault]} {...props}>
      <Text style={[styles.text, active ? styles.activeText : styles.outlinedText]}>{text}</Text>
    </Button>
  );
}

ActiveButton.propTypes = {
  active: PropTypes.bool,
  text: PropTypes.string.isRequired,
};

ActiveButton.defaultProps = {
  active: false,
};

export default ActiveButton;
