import React, { useEffect, useState } from "react";
import { StyleSheet, Image, TouchableOpacity, Text } from 'react-native';
import { Container, Content, View } from "native-base";
import { Col, Row } from "react-native-easy-grid";
import MaterialIcon from 'react-native-vector-icons/MaterialIcons';
import HeaderProgressBar from '../../components/HeaderProgressBar';
import Header from "../../components/Header";
import Typography from "../../components/Typography";
import global from "../../utils/GlobalStyles";
import Illustration from "../../assets/images/email-verification-illu.png";
import color from "../../utils/Color";

const styles = StyleSheet.create({
  resendContents: {
    marginTop: 24,
    height: 40,
  },
});

let timer = null;
const EmailVerification = () => {
  const [timerCount, setTimerCount] = useState(180);
  const [timerStart, setTimerStart] = useState(false);

  useEffect(() => setTimerStart(true)); /* Start Timer after the page load */

  useEffect(() => {
    if (timerStart) {
      timer = setTimeout(() => {
        setTimerCount(timerCount - 1);
      }, 1000);
    }
    return () => clearTimeout(timer);
  }, [timerStart, timerCount]);

  useEffect(() => {
    if (timerCount <= 0) {
      clearTimeout(timer);
      setTimerStart(false);
    }
  }, [timerCount]);

  const handleResendBtn = () => {
    setTimerCount(180);
    setTimerStart(true);
  };

  return (
    <Container>
      <HeaderProgressBar progressVal={90} />
      <Header logo menu back transparent />
      <Content style={global.wrapper}>
        <View style={global.content}>
          <Typography type="title2" text="Email Verification" />

          <View style={{ marginTop: 24 }}>
            <Typography text="We've sent an email to n***@s*******.com. Please click the [NAME OF BUTTON] button when you get it to change your password." type="body" />
          </View>

          <View style={styles.resendContents}>
            { timerCount > 0
              ? <Typography text={`Resend Email (${timerCount}s)`} type="body" />
              : (
                <View>
                  <TouchableOpacity onPress={handleResendBtn}>
                    <Text style={{ color: color.orangeRed }}>Resend Email</Text>
                  </TouchableOpacity>
                  <Row style={{ marginTop: 6 }}>
                    <Col size={1}>
                      <MaterialIcon name="info-outline" size={24} />
                    </Col>
                    <Col size={9} style={{ marginTop: 3 }}>
                      <Typography text="We've sent a new verification link to your email." type="body" />
                    </Col>
                  </Row>
                </View>
              )}
          </View>

          <View style={{ marginTop: -24 }}>
            <Image resizeMode="contain" style={{ height: 400, width: "100%" }} source={Illustration} />
          </View>

        </View>
      </Content>
    </Container>
  );
};

export default EmailVerification;
