import React from 'react';
import PropTypes from "prop-types";
import { StyleSheet, TouchableOpacity } from 'react-native';
import { Container, Content, View } from "native-base";
import { Col, Row, Grid } from "react-native-easy-grid";
import ToggleSwitch from "../../components/ToggleSwitch";
import Header from "../../components/Header";
import Typography from '../../components/Typography';
import global from "../../utils/GlobalStyles";

import SmsIcon from "../../assets/images/sms.svg";
import EmailIcon from "../../assets/images/email.svg";
import NotifIcon from "../../assets/images/notif.svg";

const styles = StyleSheet.create({
  icon: {
    width: 30,
    height: 30,
    marginRight: 20,
  },
  iconText: {
    marginTop: 0,
  },
  colRow: {
    borderBottomColor: 'rgba(229,229,229,1)',
    borderBottomWidth: 1,
    paddingVertical: 30,
    flex: 1,
    alignItems: 'center',
    paddingHorizontal: 24,
  },
  borderTop: {
    borderTopColor: 'rgba(229,229,229,1)',
    borderTopWidth: 1,
  },
  toggleContainer: {
    position: 'absolute',
    right: 24,
  },
  button: {
    color: 'rgba(255,55,55,1)',
  },
  infoContainer: {
    marginTop: 30,
    paddingHorizontal: 24,
  },
});

const CommunicationPreference = ({ navigation }) => (
  <Container>
    <Header back backAction={() => navigation.navigate("Profile")} transparent />

    <Content style={global.wrapper}>
      <View>
        <View style={{ paddingHorizontal: 24, paddingTop: 24 }}>
          <Typography type="title2" text="Communication" />
          <Typography type="title2" text="Preferences" />
        </View>

        <Grid style={[global.body, { marginLeft: 0 }]}>
          <Row size={4}>
            <Col style={[global.row, styles.colRow, styles.borderTop]}>
              <NotifIcon style={styles.icon} />
              <Typography style={styles.iconText} type="body-bold" text="Push Notifications" />
              <View style={styles.toggleContainer}>
                <ToggleSwitch
                  value
                  onSyncPress={(state) => null}
                />
              </View>
            </Col>
          </Row>
          <Row size={4}>
            <Col style={[global.row, styles.colRow]}>
              <EmailIcon style={styles.icon} />
              <Typography style={styles.iconText} type="body-bold" text="Email" />
              <View style={styles.toggleContainer}>
                <ToggleSwitch
                  value={false}
                  onSyncPress={(state) => null}
                />
              </View>
            </Col>
          </Row>
          <Row size={4}>
            <Col style={[global.row, styles.colRow]}>
              <SmsIcon style={styles.icon} />
              <Typography style={styles.iconText} type="body-bold" text="SMS" />
              <View style={styles.toggleContainer}>
                <ToggleSwitch
                  value={false}
                  onSyncPress={(state) => null}
                />
              </View>
            </Col>
          </Row>
        </Grid>

        <View style={styles.infoContainer}>
          <Typography style={{ fontSize: 14 }} type="body" text="Singlife complies with the Philippinesâ€™ data protection laws. Your personal details are safe with us. For more information, please read our " />
          <TouchableOpacity>
            <Typography style={[styles.button, { fontSize: 14 }]} type="body" text="Privacy Policy" />
          </TouchableOpacity>
        </View>
      </View>
    </Content>

  </Container>
);

CommunicationPreference.propTypes = {
  navigation: PropTypes.objectOf(PropTypes.any).isRequired,
};

export default CommunicationPreference;
