import React, { useState } from "react";
import PropTypes from "prop-types";
import { Container, Content, View } from "native-base";
import { Col, Row, Grid } from "react-native-easy-grid";

import { Formik } from "formik";
import * as Yup from "yup";

import Header from "../../components/Header";
import Footer from "../../components/Footer";
import Typography from "../../components/Typography";
import Button from "../../components/Button";
import TextField from "../../components/TextField";
import HeaderProgressBar from '../../components/HeaderProgressBar';

import global from "../../utils/GlobalStyles";

const validationSchema = Yup.object().shape({
	address: Yup.string().required("This field is required"),
	province: Yup.string().required("This field is required"),
	city: Yup.string().required("This field is required"),
	barangay: Yup.string().required("This field is required"),
	zipcode: Yup.string().required("This field is required"),
});

export default function Address({ navigation }) {
	const [focus, setFocus] = useState({ address: false, province: false, city: false, barangay: false, zipcode: false });

	const [initialValues, setInitialValues] = useState({
		address: "",
		province: "",
		city: "",
		barangay: "",
		zipcode: "",
	});

	const onFocus = (_fieldName) => {
		setFocus({ ...focus, [_fieldName]: true });
	};

	const onBlur = (_fieldName) => {
		setFocus({ ...focus, [_fieldName]: false });
	};

	const [isValid, setIsValid] = useState(true);

	return (
		<Container>
			<Header back transparent />

			<Content style={global.wrapper}>
				<View style={global.content}>

					<Formik
						enableReinitialize
						initialValues={initialValues}
						validationSchema={validationSchema}
						onSubmit={(values, { setSubmitting }) => {
							setTimeout(() => {
								console.log(values);
								setSubmitting(false);
							}, 400);
						}}
					>
						{({
							values,
							errors,
							touched,
							handleChange,
							handleBlur,
							handleSubmit,
							handleReset,
						}) => (
								<Grid style={global.body}>
									<Row size={4}>
										<Col>
											<TextField
												label="House/Unit No., Street Name, Subdivision/Village"
												focus={focus.address}
												onFocus={() => onFocus("address")}
												onBlur={() => {
													onBlur("address");
													handleBlur("address");
												}}
												value={values.address}
												onChangeText={handleChange("address")}
												multiline
												numberOfLines={3}
											/>
										</Col>
									</Row>

									<Row size={4} style={global.fieldSeparator}>
										<Col>
											<TextField
												label="Province"
												focus={focus.province}
												onFocus={() => onFocus("province")}
												onBlur={() => {
													onBlur("province");
													handleBlur("province");
												}}
												value={values.province}
												onChangeText={handleChange("province")}
											/>
										</Col>
									</Row>

									<Row size={4} style={global.fieldSeparator}>
										<Col>
											<TextField
												label="City/Municipality"
												focus={focus.city}
												onFocus={() => onFocus("city")}
												onBlur={() => {
													onBlur("city");
													handleBlur("city");
												}}
												value={values.city}
												onChangeText={handleChange("city")}
											/>
										</Col>
									</Row>

									<Row size={4} style={global.fieldSeparator}>
										<Col>
											<TextField
												label="Barangay"
												focus={focus.barangay}
												onFocus={() => onFocus("barangay")}
												onBlur={() => {
													onBlur("barangay");
													handleBlur("barangay");
												}}
												value={values.barangay}
												onChangeText={handleChange("barangay")}
											/>
										</Col>
									</Row>

									<Row size={4} style={global.fieldSeparator}>
										<Col>
											<TextField
												label="Zipcode"
												focus={focus.zipcode}
												onFocus={() => onFocus("zipcode")}
												onBlur={() => {
													onBlur("zipcode");
													handleBlur("zipcode");
												}}
												value={values.zipcode}
												onChangeText={handleChange("zipcode")}
											/>
										</Col>
									</Row>

								</Grid>
							)}
					</Formik>
				</View>
			</Content>

			<Footer>
				<Button disabled={!isValid} full variant="default-outlined" text="SAVE" />
			</Footer>
		</Container>
	);
}

Address.propTypes = {
	navigation: PropTypes.objectOf(PropTypes.any).isRequired,
};
