/* eslint-disable react/forbid-prop-types */
import React from "react";
import PropTypes from "prop-types";
import { View, StyleSheet, TouchableOpacity } from "react-native";
import { Header as NativeHeader, Text } from "native-base";

import MaterialIcon from "react-native-vector-icons/MaterialIcons";
import HamburgerIcon from "../assets/images/hamburger.svg";

import HeaderProgressBar from './HeaderProgressBar';
import Logo from "../assets/images/logo.svg";
import color from "../utils/Color";
import global from "../utils/GlobalStyles";

const styles = StyleSheet.create({
  root: {
    backgroundColor: color.white,
    borderBottomWidth: 1,
    borderBottomColor: color.veryLightPink,
    elevation: 0,
    height: "auto",
  },
  withProgress: {
    paddingTop: 0,
  },
  transparent: {
    borderBottomWidth: 0,
  },
  content: {
    alignItems: "center",
    flex: 1,
    flexDirection: "row",
    height: 90,
    justifyContent: "space-between",
    paddingHorizontal: 10,
  },
  close: {
    alignItems: "center",
    flex: 1,
    flexDirection: "row",
    height: 90,
    justifyContent: "flex-end",
    paddingHorizontal: 10,
  },
  icon: {
    margin: -6,
    padding: -6,
  },
});

export default function Header({ close, closeAction, logo, menu, menuAction, back, backAction, transparent, progress, progressVal }) {
  const headerMenu = () => {
    if (close) {
      return (
        <TouchableOpacity style={global.iconButton} onPress={closeAction}>
          <Text style={global.textButton}>CLOSE</Text>
        </TouchableOpacity>
      );
    }

    if (back) {
      return (
        <TouchableOpacity transparent style={global.iconButton} onPress={backAction}>
          <MaterialIcon style={[global.iconColor, styles.icon]} name="arrow-back" size={40} />
        </TouchableOpacity>
      );
    }

    return (
      <React.Fragment>
        {logo && <Logo />}
        {menu && <TouchableOpacity transparent onPress={menuAction} style={global.iconButton}><HamburgerIcon /></TouchableOpacity>}
      </React.Fragment>
    );
  };

  return (
    <React.Fragment>
      {progress && (
        <HeaderProgressBar progressVal={progressVal} />
      )}

      <NativeHeader
        androidStatusBarColor={color.white}
        iosBarStyle="dark-content"
        transparent={transparent}
        style={[styles.root, transparent && styles.transparent, progress && styles.withProgress]}
      >
        <View style={!close ? styles.content : styles.close}>
          {headerMenu()}
        </View>
      </NativeHeader>
    </React.Fragment>
  );
}

Header.propTypes = {
  logo: PropTypes.bool,
  menu: PropTypes.bool,
  menuAction: PropTypes.func,
  close: PropTypes.bool,
  closeAction: PropTypes.func,
  back: PropTypes.bool,
  backAction: PropTypes.func,
  transparent: PropTypes.bool,
  progress: PropTypes.bool,
  progressVal: PropTypes.number,
};

Header.defaultProps = {
  logo: false,
  menu: false,
  menuAction: () => {},
  close: false,
  closeAction: () => {},
  back: false,
  backAction: () => {},
  transparent: false,
  progress: false,
  progressVal: 0,
};
