import React from "react";
import PropTypes from "prop-types";
import { Image, TouchableOpacity } from "react-native";
import { Container, Content, View } from "native-base";
import { Col, Row, Grid } from "react-native-easy-grid";

import Header from "../../components/Header";
import Typography from "../../components/Typography";

import Illustration from "../../assets/images/success-illu.png";
import global from "../../utils/GlobalStyles";

export default function WaivedPassword({ navigation }) {
  return (
    <Container>
      <Header logo={false} menu={false} close transparent />

      <Content>
        <View>
          <View style={global.content}>
            <Typography text="Youâ€™ve waived changing your current password." type="title2" />

            <Grid style={global.body}>
              <Row size={4}>
                <Col>
                  <Typography text="We will remind you to update your password again on [DATE]. You can also" type="body" />
                  <TouchableOpacity style={global.link}><Typography text="change your password" type="body-link" /></TouchableOpacity>
                  <Typography text="anytime" type="body" />
                </Col>
              </Row>
            </Grid>
          </View>

          <Image resizeMode="cover" style={[global.illuSeparator, { height: 400, width: "100%" }]} source={Illustration} />
        </View>
      </Content>
    </Container>
  );
}

WaivedPassword.propTypes = {
  navigation: PropTypes.objectOf(PropTypes.any).isRequired,
};
