import React from "react";
import PropTypes from "prop-types";
import { StyleSheet } from "react-native";
import { Item, Input, Label } from "native-base";

import { RFValue } from "react-native-responsive-fontsize";
import color from "../utils/Color";

const styles = StyleSheet.create({
  item: {
    borderColor: "transparent",
    margin: 0,
    padding: 0,
    paddingBottom: 10,
  },
  error: {
    borderBottomWidth: 1,
    borderColor: color.orangeRed,
    borderStyle: "solid",
  },
  label: {
    fontSize: RFValue(28),
    color: color.veryLightPink,
    fontWeight: "normal",
    fontFamily: "SharpSansSemibold",
  },
  labelUnfocus: {
    color: color.veryLightPink,
    fontFamily: "SharpSansSemibold",
    fontSize: RFValue(28),
  },
  labelFocus: {
    fontFamily: "SharpSansSemibold",
    color: "rgb(204, 204, 204)",
    fontSize: RFValue(12),
    letterSpacing: 0.5,
  },
  input: {
    color: color.black,
    marginTop: 10,
    fontFamily: "SharpSansSemibold",
    fontSize: RFValue(20),
    textDecorationLine: "none",
  },
});

function CustomTextField({ label, focus, icon, showPassword, error, ...props }) {
  const properties = { ...props };

  return (
    <React.Fragment>
      <Item floatingLabel style={[styles.item, error && styles.error]}>
        <Label style={[styles.label, focus || properties.value !== "" ? styles.labelFocus : styles.labelUnfocus]}>{label}</Label>
        <Input style={styles.input} {...props} />
      </Item>
    </React.Fragment>
  );
}

CustomTextField.propTypes = {
  label: PropTypes.string.isRequired,
  focus: PropTypes.bool,
  icon: PropTypes.node,
  showPassword: PropTypes.func,
  error: PropTypes.oneOfType([PropTypes.string, PropTypes.bool]),
};

CustomTextField.defaultProps = {
  focus: false,
  icon: "",
  showPassword: () => { },
  error: false,
};

export default CustomTextField;
