import React, { useState } from "react";
import PropTypes from "prop-types";
import { Container, Content, View } from "native-base";
import { Col, Row, Grid } from "react-native-easy-grid";

import Header from "../../components/Header";
import Footer from "../../components/Footer";
import Button from "../../components/Button";
import ActiveButton from "../../components/ActiveButton";
import Typography from "../../components/Typography";
import SwipeModal from "../../components/SwipeModal";

import global from "../../utils/GlobalStyles";

export default function Fatca({ navigation }) {
  const [fatca, setFatca] = useState("");
  const [alert, setAlert] = useState(false);

  const answerFatca = (_option) => {
    setFatca(_option);
  };

  const openAlert = () => {
    setAlert(true);
  };

  const closeAlert = () => {
    setAlert(false);
  };

  const nextStep = () => {
    if (fatca === "yes") {
      openAlert();
    } else {
      navigation.navigate("OnboardingGender");
    }
  };

  return (
    <Container>
      <Header back transparent backAction={() => navigation.goBack()} progress />

      <Content style={global.wrapper}>
        <View style={global.content}>
          <Typography type="title2" text="Do you have a US residency/citizenship or tax identification number?" />

          <Grid>
            <Row size={4} style={global.body}>
              <Col>
                <Typography text="US citizens/residents are not eligible to apply for an account at this time." type="body" />
              </Col>
            </Row>

            <Row size={4} style={global.body}>
              <Col style={{ paddingRight: 10 }}>
                <ActiveButton onPress={() => answerFatca("yes")} full active={fatca === "yes"} text="YES" />
              </Col>

              <Col style={{ paddingLeft: 10 }}>
                <ActiveButton onPress={() => answerFatca("no")} full active={fatca === "no"} variant="default-outlined" text="NO" />
              </Col>
            </Row>
          </Grid>
        </View>
      </Content>

      <Footer>
        <Button full disabled={fatca === ""} variant="primary-contained" text="Next" onPress={nextStep} />
      </Footer>

      <SwipeModal state={alert} onClose={closeAlert}>
        <View style={global.modalView}>
          <Typography text="Please be informed that US citizens and residents are not eligible to apply at this time." type="body" />
        </View>
      </SwipeModal>

      {alert && (
        <View style={global.modalFooterView}>
          <Button full disabled={fatca === ""} variant="primary-contained" text="OKAY" onPress={closeAlert} />
        </View>
      )}

    </Container>
  );
}

Fatca.propTypes = {
  navigation: PropTypes.objectOf(PropTypes.any).isRequired,
};
