import React from "react";
import PropTypes from "prop-types";
import { Image } from "react-native";
import { Container, Content, View } from "native-base";

import Header from "../../components/Header";
import Footer from "../../components/Footer";
import Button from "../../components/Button";
import Typography from "../../components/Typography";

import Illustration from "../../assets/images/success-illu.png";
import global from "../../utils/GlobalStyles";

export default function SuccessfulReset({ navigation }) {
  return (
    <Container>
      <Header logo={false} menu={false} close={false} transparent />

      <Content>
        <View>
          <View style={global.content}>
            <Typography text="Youâ€™ve successfully reset your password." type="title2" />
          </View>

          <Image resizeMode="cover" style={[global.illuSeparator, { height: 400, width: "100%" }]} source={Illustration} />
        </View>
      </Content>

      <Footer>
        <Button onPress={() => navigation.navigate("Login")} full variant="default-outlined" text="LOG IN" />

        <View style={global.buttonSeparator}>
          <Button onPress={() => navigation.navigate("FailedResetPassword")} full variant="default-outlined" text="SHOW FAILED RESET" />
        </View>
      </Footer>
    </Container>
  );
}

SuccessfulReset.propTypes = {
  navigation: PropTypes.objectOf(PropTypes.any).isRequired,
};
