import React, { useState } from "react";
import PropTypes from "prop-types";
import { Dimensions, StyleSheet, TouchableOpacity, ImageBackground } from "react-native";
import { Container, Content, View, Text, ListItem, Left } from "native-base";
import { Col, Row, Grid } from "react-native-easy-grid";
import EvilIcon from "react-native-vector-icons/EvilIcons";
import AntIcon from "react-native-vector-icons/AntDesign";
import { RFValue } from "react-native-responsive-fontsize";

import Carousel, { Pagination } from 'react-native-snap-carousel';

import Header from "../../components/Header";
import Typography from "../../components/Typography";

import Illustration from "../../assets/images/illustration.jpg";

import color from "../../utils/Color";
import global from "../../utils/GlobalStyles";

const sliderWidth = Dimensions.get("window").width;
const itemWidth = Math.round(sliderWidth * 0.92);

const styles = StyleSheet.create({
  slideWrapper: {
    height: "100%",
  },
  slideContent: {
    borderColor: color.veryLightPink,
    borderRadius: 4,
    borderWidth: 1,
    height: "80%",
    marginHorizontal: 5,
    padding: 26,
    position: "relative",
  },
  slideNoBorder: {
    alignItems: "center",
    flexDirection: "column",
    height: "80%",
    marginHorizontal: 5,
    padding: 5,
    paddingVertical: 26,
    position: "relative",
  },
  slideDisabled: {
    opacity: 0.3,
  },
  slideContainer: {
    backgroundColor: "transparent",
  },
  slideDot: {
    width: 10,
    height: 10,
    borderRadius: 5,
    marginHorizontal: 1,
    backgroundColor: "#ff3737",
  },
  slideInactiveDot: {
    backgroundColor: "rgba(204, 204, 204, 0.92)",
  },
  bottomButton: {
    bottom: 26,
    left: 26,
    position: "absolute",
  },
  list: {
    alignItems: "flex-start",
    width: "100%",
  },
  listIcon: {
    color: color.softGreen,
    marginRight: 10,
  },
  listText: {
    fontFamily: "SharpSansSemibold",
    fontSize: RFValue(14),
    fontWeight: "600",
  },
  listLink: {
    color: color.orangeRed,
    fontFamily: "SharpSansSemibold",
    fontSize: RFValue(14),
    fontWeight: "600",
    marginHorizontal: 5,
  },
});

export default function EmergencyFund({ navigation }) {
  const [activeIndex, setActiveIndex] = useState(0);
  const doneStep = 1;
  const cards = [
    {
      step: 1,
      title: "Create my user account",
      text: "Goodbye piggy bank, hello Singlife. Creating one is the first step to unlocking the features of your app. ",
      button: <TouchableOpacity style={global.iconButton} onPress={() => navigation.navigate("OnboardingCreateAccount")}><Text style={global.textButton}>START</Text></TouchableOpacity>,
    },
    {
      step: 2,
      title: "Verify my identity",
      text: "We need to confirm that it’s really you.",
      button: "",
    },
    {
      step: 3,
      title: "Back up my emergency fund",
      text: "Protect From Emergency is your emergency fund goal in insurance form. It’s simple, really: it’s a back up for your back up. ",
      button: "",
    },
    {
      step: 4,
      title: "Cash in to my emergency fund",
      text: "Start saving towards your emergency fund goal! Don't forget: your first premium will be automatically deducted on [DATE].",
      button: "",
    },
    {
      step: "",
      title: "",
      text: "",
      button: "",

    },
  ];

  const renderItem = ({ item, index }) => {
    if (item.step !== "") {
      return (
        <ImageBackground source={Illustration} style={[styles.slideContent, (doneStep !== item.step && doneStep <= item.step) && styles.slideDisabled]}>
          {item.step !== "" && (
            <Typography text={`STEP ${index + 1}`} type="body" />
          )}

          {item.title !== "" && (
            <View style={global.buttonSeparator}>
              <Typography text={item.title} type="title2" />
            </View>
          )}

          {item.text !== "" && (
            <View style={global.buttonSeparator}>
              <Typography text={item.text} type="body" />
            </View>
          )}

          {item.button !== "" && (
            <View style={styles.bottomButton}>
              {item.button}
            </View>
          )}
        </ImageBackground>
      );
    }

    return (
      <View style={styles.slideNoBorder}>
        <EvilIcon name="image" size={80} />

        <View style={styles.list}>
          <ListItem icon>
            <Left>
              <AntIcon style={styles.listIcon} name="checkcircleo" size={24} />

              <Text style={styles.listText}>Earn 2%</Text>
              <TouchableOpacity onPress={() => navigation.navigate("OnboardingInterestRate")}><Text style={styles.listLink}>interest rate</Text></TouchableOpacity>
              <Text style={styles.listText}>p.a., up to 5%</Text>
            </Left>
          </ListItem>

          <ListItem icon>
            <Left>
              <AntIcon style={styles.listIcon} name="checkcircleo" size={24} />
              <Text style={styles.listText}>Qualify for a Singlife Card</Text>
            </Left>
          </ListItem>
        </View>
      </View>
    );
  };

  return (
    <Container>
      <Header back backAction={() => navigation.navigate("Onboarding")} transparent />

      <Content contentContainerStyle={{ flexGrow: 1 }}>
        <View style={styles.slideWrapper}>
          <View style={[global.content, { paddingBottom: 0 }]}>
            <Typography text="Let's start your emergency fund." type="title2" />

            <Grid style={global.body}>
              <Row size={4}>
                <Col>
                  <Typography text="A better, smarter emergency fund is within your reach through 4 easy steps. Here’s what you need to do:" type="body" />
                </Col>
              </Row>
            </Grid>
          </View>

          <View style={[styles.slideWrapper, { paddingBottom: 40 }]}>
            <Pagination
              dotsLength={cards.length}
              activeDotIndex={activeIndex}
              containerStyle={styles.slideContainer}
              dotStyle={styles.slideDot}
              inactiveDotStyle={styles.slideInactiveDot}
              inactiveDotOpacity={1}
              inactiveDotScale={1}
            />

            <Carousel
              data={cards}
              sliderWidth={sliderWidth}
              itemWidth={itemWidth}
              renderItem={renderItem}
              onBeforeSnapToItem={index => setActiveIndex(index)}
              inactiveSlideScale={1}
              activeSlideAlignment="center"
            />
          </View>
        </View>
      </Content>
    </Container>
  );
}

EmergencyFund.propTypes = {
  navigation: PropTypes.objectOf(PropTypes.any).isRequired,
};
