import React, { useState } from 'react';
import { View, StyleSheet } from 'react-native';
import { TextInputMask } from 'react-native-masked-text';
import { RFValue } from "react-native-responsive-fontsize";

import Error from "./Error";

import global from "../utils/GlobalStyles";
import color from "../utils/Color";

const OtpField = (props) => {
  const styles = StyleSheet.create({
    input: {
      marginTop: 1.5,
      width: '100%',
      fontSize: RFValue(28),
      fontFamily: 'SharpSansSemibold',
    },
    inputActive: {
      marginTop: 0,
      width: '100%',
      fontSize: RFValue(28),
      letterSpacing: 10,
      fontFamily: 'SharpSansSemibold',
    },
    placeholder: {
      fontSize: RFValue(28),
      fontFamily: 'SharpSansSemibold',
    },
    textError: {
      color: 'rgba(197,0,6,1)',
      fontSize: RFValue(14),
      marginLeft: 10,
    },
    textInfo: {
      color: '#000',
      fontSize: 16,
      marginLeft: 31,
    },
    textButton: {
      color: '#ff3737',
      fontSize: 16,
      marginLeft: 31,
      textDecorationLine: 'underline',
    },
    errorIcon: {
      height: 20,
      width: 20,
    },
    hr: {
      backgroundColor: color.orangeRed,
      height: 1.3,
      width: '100%',
    },
  });

  const { OTPValue, onChangeOTPNum, enableNext } = props;
  const [isActive, setIsActive] = useState(false);
  const [isValid, setIsValid] = useState(false);
  const [isTouched, setIsTouched] = useState(false);

  const validateOTP = (OTP) => {
    const OTPRaw = OTP.replace(/ /g, '');
    setIsTouched(true);
    onChangeOTPNum(OTPRaw);
    if (OTPRaw.length === 6) {
      if (OTPRaw === "123456") {
        enableNext(true);
        setIsValid(true);
      } else {
        enableNext(false);
        setIsValid(false);
      }
    } else {
      enableNext(false);
    }
  };

  return (
    <View style={{ width: "100%" }}>
      <View style={global.row}>
        <TextInputMask
          maxLength={6}
          style={isActive ? styles.inputActive : styles.input}
          placeholder="6 digit code"
          value={OTPValue}
          type="cel-phone"
          options={{
            withDDD: true,
            dddMask: '999999',
          }}
          onChangeText={text => {
            setIsActive(true);
            validateOTP(text);
          }}
        />
      </View>

      {isValid === false && isTouched && (
        <View style={{ marginTop: 10 }}>
          <View style={styles.hr} />
          <Error text="Enter the correct OTP sent to your mobile." />
        </View>
      )}
    </View>

  );
};

export default OtpField;
