import React, { useEffect, useState } from 'react';
import { StyleSheet, Image } from 'react-native';
import { Container, Content, View } from "native-base";
import { Col, Row, Grid } from "react-native-easy-grid";
import Header from "../../components/Header";
import Footer from "../../components/Footer";
import Button from "../../components/Button";
import OtpField from '../../components/OtpField';
import Typography from '../../components/Typography';
import global from "../../utils/GlobalStyles";
import otpIllu from "../../assets/images/otp-illu.png";

const styles = StyleSheet.create({
  textContainer: {
    marginTop: 89,
    marginLeft: 24,
    marginRight: 30,
  },
  headerText: {
    lineHeight: 30,
    marginTop: 4,
    fontSize: 24,
    fontFamily: 'SharpSansSemibold',
  },
  lineText: {
    lineHeight: 30,
    marginTop: 1.5,
    fontSize: 24,
    fontFamily: 'SharpSansSemibold',
  },
  fieldContainer: {
    flexDirection: 'row',
    marginTop: 20,
  },
  illu: {
    width: 250,
    height: 185,
  },
});

const otp = ({ navigation }) => {
  const [OTPNum, setOTPNum] = useState("");
  const [isValid, setIsValid] = useState(false);
  const [countDown, setCountdown] = useState(180);
  const headerText = `Please enter the code we've just sent to +63 917 000 0000.`;


  // useEffect(() => {
  //   const timer = setTimeout(() => {
  //     setCountdown(countDown - 1);
  //   }, 1000);

  //   return () => clearTimeout(timer);
  // });

  return (
    <Container>
      <Header back transparent />

      <Content style={global.wrapper}>
        <View style={global.content}>
          <View style={{}}>
            <Typography style={{ marginLeft: 24 }} type="title2" text={headerText} />
          </View>

          <Grid style={global.body}>
            <Row size={4}>
              <Col>
                <OtpField enableNext={setIsValid} onChangeOTPNum={setOTPNum} OTPValue={OTPNum} />
              </Col>
            </Row>

            <Row size={4} style={global.fieldSeparator}>
              <Col style={{marginLeft: 15 }}>
                <Typography text={`Resend OTP (${countDown}) s`} type="body" />
              </Col>
            </Row>

            <Row size={4}>
              <Col style={{ alignItems: 'flex-end', bottom: 0, marginTop: 150, marginRight: -20 }}>
                <Image style={styles.illu} source={otpIllu} />
              </Col>
            </Row>

          </Grid>
        </View>

      </Content>
      <Footer>
        <Button onPress={() => navigation.navigate("Email")} disabled={!isValid} full variant="default-outlined" text="Next" />
      </Footer>

    </Container>
  );
};

export default otp;
