import React from "react";
import PropTypes from "prop-types";
import { TouchableOpacity, StyleSheet } from "react-native";
import { Container, Content, View } from "native-base";
import { Col, Row, Grid } from "react-native-easy-grid";

import Header from "../../components/Header";
import Footer from "../../components/Footer";
import Typography from "../../components/Typography";

import ArrowIcon from "../../assets/images/path.svg";
import CardIcon from "../../assets/images/cardicon.svg";
import ProfileIcon from "../../assets/images/profileinfo.svg";
import CommunicationIcon from "../../assets/images/communicationicon.svg";
import PasswordIcon from "../../assets/images/passwordicon.svg";
import global from "../../utils/GlobalStyles";

const styles = StyleSheet.create({
  menu: {
    width: "100%",
    height: 42,
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "space-between",
    paddingHorizontal: 5,
  },
  menuIcon: {
    marginRight: 10,
  },
  menuText: {
    flex: 2,
  },
  menuArrow: {
    width: 7,
    height: 12,
  },
});

const nickname = "Singlife";
const version = "1.0";
const menu = [
  {
    icon: <CardIcon style={styles.menuIcon} />,
    text: "CUSTOMER ID",
    url: "ProfileCustomerId",
    position: "upper",
  },
  {
    icon: <ProfileIcon style={styles.menuIcon} />,
    text: "PROFILE INFORMATION",
    url: "ProfileInformation",
    position: "upper",
  },
  {
    icon: <CommunicationIcon style={styles.menuIcon} />,
    text: "COMMUNICATION PREFERENCES",
    url: "ProfileComPreference",
    position: "lower",
  },
  {
    icon: <PasswordIcon style={styles.menuIcon} />,
    text: "PASSWORD & SECURITY",
    url: "PasswordSecurity",
    position: "lower",
  },
];

export default function Profile({ navigation }) {
  const renderMenu = (item, position) => item.map(row => {
    if (position === row.position) {
      return (
        <Row size={4} style={global.buttonSeparator} key={`menu-${row.text}`}>
          <Col>
            <TouchableOpacity style={styles.menu} onPress={() => navigation.navigate(row.url)}>
              {row.icon}

              <View style={styles.menuText}>
                <Typography type="body-bold" text={row.text} />
              </View>

              <ArrowIcon />
            </TouchableOpacity>
          </Col>
        </Row>
      );
    }

    return false;
  });

  return (
    <Container>
      <Header back transparent />

      <Content style={global.wrapper}>
        <View style={global.content}>
          <Typography type="title2" text={`Hello ${nickname}!`} />

          <Grid style={global.body}>
            {renderMenu(menu, "upper")}

            <Row size={4} style={global.buttonSeparator}>
              <Col>
                <View style={global.rowSeparator} />
              </Col>
            </Row>

            {renderMenu(menu, "lower")}
          </Grid>
        </View>
      </Content>

      <Footer>
        <Typography type="body-bold" text={`APP VERSION ${version}`} />
      </Footer>
    </Container>
  );
}

Profile.propTypes = {
  navigation: PropTypes.objectOf(PropTypes.any).isRequired,
};
