import React, { useState } from "react";
import PropTypes from "prop-types";
import { Container, Content, View } from "native-base";
import { Col, Row, Grid } from "react-native-easy-grid";

import { Formik } from "formik";
import * as Yup from "yup";

import Header from "../../components/Header";
import Footer from "../../components/Footer";
import Typography from "../../components/Typography";
import Button from "../../components/Button";
import TextField from "../../components/TextField";

import global from "../../utils/GlobalStyles";

export default function FullName({ navigation }) {
  const [focus, setFocus] = useState({ first_name: false, middle_name: false, last_name: false });
  const [initialValues, setInitialValues] = useState({
    first_name: "",
    middle_name: "",
    last_name: "",
  });

  const onFocus = (_fieldName) => {
    setFocus({ ...focus, [_fieldName]: true });
  };

  const onBlur = (_fieldName) => {
    setFocus({ ...focus, [_fieldName]: false });
  };

  const onSubmit = (_values) => {
    navigation.navigate("OnboardingNickname");
  };

  return (
    <Container>
      <Header progress back backAction={() => navigation.goBack()} transparent />

      <Formik
        enableReinitialize
        initialValues={initialValues}
        onSubmit={(values, { setSubmitting }) => {
          setTimeout(() => {
            onSubmit(values);
            setSubmitting(false);
          }, 400);
        }}
      >
        {({
          values,
          handleBlur,
          setFieldValue,
          handleSubmit,
        }) => (
          <React.Fragment>
            <Content style={global.wrapper}>
              <View style={global.content}>
                <Typography text="Let's start creating your account. What's your name?" type="title2" />

                <Grid style={global.body}>
                  <Row size={4}>
                    <Col>
                      <TextField
                        maxLength={40}
                        label="First Name"
                        focus={focus.first_name}
                        value={values.first_name}
                        onFocus={() => onFocus("first_name")}
                        onBlur={() => {
                          onBlur("first_name");
                          handleBlur("first_name");
                        }}
                        onChangeText={(value) => {
                          const regex = new RegExp(/^[a-zA-Z0-9]*$/);

                          if (regex.test(value)) {
                            setFieldValue("first_name", value);
                          }
                        }}
                      />
                    </Col>
                  </Row>

                  <Row size={4} style={global.fieldSeparator}>
                    <Col>
                      <TextField
                        maxLength={40}
                        label="Middle Name (Optional)"
                        focus={focus.middle_name}
                        value={values.middle_name}
                        onFocus={() => onFocus("middle_name")}
                        onBlur={() => {
                          onBlur("middle_name");
                          handleBlur("middle_name");
                        }}
                        onChangeText={(value) => {
                          const regex = new RegExp(/^[a-zA-Z0-9]*$/);

                          if (regex.test(value)) {
                            setFieldValue("middle_name", value);
                          }
                        }}
                      />
                    </Col>
                  </Row>

                  <Row size={4} style={global.fieldSeparator}>
                    <Col>
                      <TextField
                        maxLength={40}
                        label="Last Name"
                        focus={focus.last_name}
                        value={values.last_name}
                        onFocus={() => onFocus("last_name")}
                        onBlur={() => {
                          onBlur("last_name");
                          handleBlur("last_name");
                        }}
                        onChangeText={(value) => {
                          const regex = new RegExp(/^[a-zA-Z0-9]*$/);

                          if (regex.test(value)) {
                            setFieldValue("last_name", value);
                          }
                        }}
                      />
                    </Col>
                  </Row>
                </Grid>
              </View>
            </Content>

            <Footer>
              <Button onPress={handleSubmit} disabled={values.first_name === "" || values.last_name === ""} full variant="primary-contained" text="Next" />
            </Footer>
          </React.Fragment>
        )}
      </Formik>
    </Container>
  );
}

FullName.propTypes = {
  navigation: PropTypes.objectOf(PropTypes.any).isRequired,
};
