import React from "react";
import PropTypes from "prop-types";
import { View, StyleSheet } from "react-native";
import Icon from 'react-native-vector-icons/EvilIcons';
import { RFValue } from "react-native-responsive-fontsize";

import Typography from "./Typography";
import color from "../utils/Color";
import global from "../utils/GlobalStyles";

const styles = StyleSheet.create({
  item: {
    alignItems: "center",
    flexDirection: "row",
    marginHorizontal: 0,
    paddingVertical: 8,
  },
  icon: {
    color: color.veryLightPink,
    marginRight: 10,
  },
  iconSuccess: {
    color: color.softGreen,
    marginRight: 10,
  },
});

function PasswordChecklist({ password }) {
  const checklist1 = () => {
    if (password.length > 7) {
      return (
        <View style={styles.item}>
          <Icon name="check" style={styles.iconSuccess} size={RFValue(30)} />
          <Typography text="8 characters minimum" type="pw2" />
        </View>
      );
    }
    return (
      <View style={styles.item}>
        <Icon name="close-o" style={styles.icon} size={RFValue(30)} />
        <Typography text="8 characters minimum" type="pw1" />
      </View>
    );
  };

  const checklist2 = () => {
    if (password.match(new RegExp("[A-Z]"))) {
      return (
        <View style={styles.item}>
          <Icon name="check" style={styles.iconSuccess} size={RFValue(30)} />
          <Typography text="One uppercase letter" type="pw2" />
        </View>
      );
    }
    return (
      <View style={styles.item}>
        <Icon name="close-o" style={styles.icon} size={RFValue(30)} />
        <Typography text="One uppercase letter" type="pw1" />
      </View>
    );
  };

  const checklist3 = () => {
    if (password.match(new RegExp("[0-9]+"))) {
      return (
        <View style={styles.item}>
          <Icon name="check" style={styles.iconSuccess} size={RFValue(30)} />
          <Typography text="One number" type="pw2" />
        </View>
      );
    }
    return (
      <View style={styles.item}>
        <Icon name="close-o" style={styles.icon} size={RFValue(30)} />
        <Typography text="One number" type="pw1" />
      </View>
    );
  };

  const checklist4 = () => {
    if (password.match(new RegExp(/[~`!@#$%^&*+=\-[\]\\';,/{}|\\":<>?]/))) {
      return (
        <View style={styles.item}>
          <Icon name="check" style={styles.iconSuccess} size={RFValue(30)} />
          <Typography text="One special character" type="pw2" />
        </View>
      );
    }
    return (
      <View style={styles.item}>
        <Icon name="close-o" style={styles.icon} size={RFValue(30)} />
        <Typography text="One special character" type="pw1" />
      </View>
    );
  };

  return (
    <View style={global.wrapper}>
      {checklist1()}
      {checklist2()}
      {checklist3()}
      {checklist4()}
    </View>
  );
}

PasswordChecklist.propTypes = {
  password: PropTypes.string,
};

PasswordChecklist.defaultProps = {
  password: "",
};

export default PasswordChecklist;
