import React from "react";
import { StyleSheet } from 'react-native';
import { Container, Content, View } from "native-base";
import Header from "../../components/Header";
import Typography from "../../components/Typography";
import global from "../../utils/GlobalStyles";
import color from "../../utils/Color";
import Card from "../../components/Card";

const styles = StyleSheet.create({
  successBanner: {
    backgroundColor: color.neutral,
    width: "100%",
    height: 240,
  },
});

const Success = () => (
  <Container>
    <Header close transparent />

    <Content style={global.wrapper}>
      <View style={global.content}>
        <Typography type="title2" text="You're on your way!" />
      </View>
      <View style={styles.successBanner} />
      <View style={global.content}>
        <Typography text="Now that we're done with introductions, we'd like to get to know you a little bit more." type="body" />

        <Card type="card-check" contents={{ title: "Create user account" }} />
        <Card type="card-steps" active contents={{ stepNo: 2, title: "Verify my identity", description: "We need to confirm that it's really you, Neri.", hasButton: true, buttonText: "START" }} />
        <Card type="card-steps" disabled contents={{ stepNo: 3, title: "Back up my emergency fund", description: "Protect From Emergency is your emergency fund goal in insurance form. It's simple, really: it's a back up for your back up." }} />
        <Card type="card-steps" disabled contents={{ stepNo: 4, title: "Cash in to my emergency fund", description: "Start saving towards your emergency fund goal! Don't forget: you first premium will be automatically deducted on [DATE]." }} />
        <Card type="card-success-info" contents={{ descriptions: ["Earn 2% (up to 5%) interest rate p.a. .", "Qualify for a Singlife Card"] }} />
      </View>
    </Content>
  </Container>
);

export default Success;
