import React, { useState } from 'react';
import { Text, View, StyleSheet } from 'react-native';
import { TextInputMask } from 'react-native-masked-text';
import { RFValue } from "react-native-responsive-fontsize";
import { TouchableOpacity } from 'react-native-gesture-handler';
import { Col, Row, Grid } from "react-native-easy-grid";

import Error from "./Error";
import Typography from "./Typography";

import color from "../utils/Color";
import global from "../utils/GlobalStyles";

const mobileField = (props) => {
  const styles = StyleSheet.create({
    input: {
      marginTop: 1.5,
      width: '100%',
      fontSize: RFValue(28),
      fontWeight: "normal",
      fontFamily: 'SharpSansSemibold',
    },
    inputActive: {
      marginTop: 0,
      width: '100%',
      fontSize: RFValue(28),
      fontWeight: "normal",
      fontFamily: 'SharpSansSemibold',
    },
    placeholder: {
      borderRightWidth: 1,
      marginRight: 12,
      paddingRight: 15,
      fontSize: RFValue(28),
      fontWeight: "normal",
      fontFamily: 'SharpSansSemibold',
    },
    textError: {
      color: 'rgba(197,0,6,1)',
      fontSize: 14,
      marginLeft: 10,
    },
    textInfo: {
      color: '#000',
      fontSize: 16,
      marginLeft: 31,
    },
    textButton: {
      color: '#ff3737',
      fontSize: 16,
      marginLeft: 31,
      textDecorationLine: 'underline',
    },
    errorIcon: {
      height: 20,
      width: 20,
    },
    hr: {
      backgroundColor: color.orangeRed,
      height: 1.3,
      width: '100%',
    },
  });

  const { mobileValue, onChangeMobileNum, enableNext } = props;
  const [isActive, setIsActive] = useState(false);
  const [isValid, setIsValid] = useState(false);

  const validateMobile = (mobile) => {
    const mobileRaw = mobile.replace(/ /g, '');

    onChangeMobileNum(mobileRaw);
    if (mobileRaw !== "") {
      if (mobileRaw.length === 10) {
        if (mobileRaw === "9170000000") {
          enableNext(true);
          setIsValid(true);
        } else {
          enableNext(false);
          setIsValid(false);
        }
      } else if (mobileRaw.length < 10) {
        enableNext(false);
        setIsValid(false);
      }
    } else {
      enableNext(false);
      setIsValid(true);
    }
  };

  return (
    <View style={{ width: "100%" }}>
      <View style={global.row}>
        <Text style={styles.placeholder}>+63</Text>
        <TextInputMask
          maxLength={12}
          style={isActive ? styles.inputActive : styles.input}
          placeholder="000 000 0000"
          value={mobileValue}
          type="cel-phone"
          options={{
            withDDD: true,
            dddMask: '999 999 9999',
          }}
          onChangeText={text => {
            setIsActive(true);
            validateMobile(text);
          }}
        />
      </View>

      {isValid === false && isActive && (
        <View style={{ marginTop: 10 }}>
          <View style={styles.hr} />
          <Error text="This mobile number is already taken." />

          <Grid style={[global.body, { marginTop: 24 }]}>
            <Row size={4} style={{ paddingLeft: 50 }}>
              <Col style={global.row}>
                <Typography text="Please enter another mobile number or " type="body" />
                <TouchableOpacity style={global.link}><Typography text="log in" type="body-link" /></TouchableOpacity>
                <Typography text="." type="body" />
              </Col>
            </Row>
          </Grid>
        </View>
      )}
    </View>

  );
};



export default mobileField;
