import React, { useState } from "react";
import { Container, Content, View } from "native-base";
import Header from "../../components/Header";
import Footer from "../../components/Footer";
import Button from "../../components/Button";
import Typography from "../../components/Typography";
import global from "../../utils/GlobalStyles";
import color from "../../utils/Color";
import CustomButton from "../../components/CustomButton";
import Webview from "../../components/Webview";

const TermsAndPrivacyPolicy = () => {
  const [openWebview, setOpenWebview] = useState(false);
  const [webviewUrl, setWebviewUrl] = useState("");

  return (
    <Container>
      <Header progress progressVal={90} back transparent />
      <Content style={global.wrapper}>
        <View style={global.content}>
          <Typography type="title2" text="Read up on Singlife's Terms of Use & Privacy Policy." />

          <View style={global.body}>
            <CustomButton
              onPress={() => {
                setOpenWebview(true);
                setWebviewUrl("https://singlife.com.ph/terms-of-use/");
              }}
              buttonType="text-arrow"
              content={{ text: "TERMS OF USE" }}
            />
            <CustomButton
              onPress={() => {
                setOpenWebview(true);
                setWebviewUrl("https://singlife.com.ph/privacy_policy/");
              }}
              buttonType="text-arrow"
              content={{ text: "PRIVACY POLICY" }}
            />
          </View>
        </View>
      </Content>

      <Footer>
        <View style={{ backgroundColor: color.veryLightPink, padding: 16, borderRadius: 4, marginBottom: 12 }}>
          <Typography type="body" text="By clicking Agree & Continue, I hereby agree and consent to the Terms of Use & Privacy Policy." />
        </View>
        <Button full variant="primary-contained" text="AGREE & CONTINUE" />
      </Footer>

      <Webview webviewOpen={openWebview} setWebviewOpen={setOpenWebview} url={webviewUrl} />
    </Container>
  );
};

export default TermsAndPrivacyPolicy;
