import React from "react";
import PropTypes from "prop-types";
import { Container, Content, View } from "native-base";
import { Col, Row, Grid } from "react-native-easy-grid";

import Header from "../../components/Header";
import Footer from "../../components/Footer";
import Button from "../../components/Button";
import Typography from "../../components/Typography";

import global from "../../utils/GlobalStyles";

export default function KeepSecure({ navigation }) {
  return (
    <Container>
      <Header logo={false} menu={false} close={false} transparent />

      <Content style={global.wrapper}>
        <View style={global.content}>
          <Typography text="Keep your account secure." type="title2" />

          <Grid style={global.body}>
            <Row size={4}>
              <Col>
                <Typography text="We recommend that you change your password once a month." type="body" />
              </Col>
            </Row>
          </Grid>
        </View>
      </Content>

      <Footer>
        <Button full variant="default-outlined" text="KEEP CURRENT PASSWORD" />

        <View style={global.buttonSeparator}>
          <Button full variant="primary-contained" text="UPDATE PASSWORD" />
        </View>
      </Footer>
    </Container>
  );
}

KeepSecure.propTypes = {
  navigation: PropTypes.objectOf(PropTypes.any).isRequired,
};
