import React, { useState } from 'react';
import { Container, Content, View } from "native-base";
import Header from "../../components/Header";
import Footer from "../../components/Footer";
import Button from "../../components/Button";
import MobileField from '../../components/MobileField';
import Typography from '../../components/Typography';
import global from "../../utils/GlobalStyles";

const mobileNumber = ({ navigation }) => {
  const [mobileNum, setMobileNum] = useState("");
  const [isValid, setIsValid] = useState(false);

  return (
    <Container>
      <Header progress progressVal={25} back backAction={() => navigation.goBack()} transparent />

      <Content style={global.wrapper}>
        <View style={global.content}>
          <Typography type="title2" text="What's your mobile number?" />

          <View style={global.body}>
            <MobileField enableNext={setIsValid} onChangeMobileNum={setMobileNum} mobileValue={mobileNum} />
          </View>
        </View>
      </Content>

      <Footer>
        <Button onPress={() => navigation.navigate("OnboardingOtp")} disabled={!isValid} full variant="primary-contained" text="Next" />
      </Footer>
    </Container>
  );
};

export default mobileNumber;
